mapper.select:
SELECT EXISTS (
    SELECT user_no FROM user_project
    WHERE user_no = #{userNo}
        AND project_no = #{projectNo}
)AS RESULT

SELECT EXISTS (
SELECT 1
FROM project
WHERE project_no = #{projectNo}
    AND leader_no = #{signedUserNo}
)AS RESULT

mapper.update:
UPDATE project
SET title = #{title}
    , description = #{description}
    , start_at = #{startAt}
    , deadline = #{deadLine}
WHERE project_no = #{projectNo}

mapper.delete:
DELETE FROM user_project
WHERE project_no = #{projectNo}
AND user_no IN

    #{userNo}
