mapper.insert:
INSERT INTO `user`
SET user_id = #{userId}
    , password = #{password}
    , nickname = #{nickname}
    , email = #{email}
    , first_login = TRUE

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM `user`
    WHERE email=#{email}
)

SELECT EXISTS (
    SELECT 1 FROM `user`
    WHERE nickname=#{nickname}
)

SELECT EXISTS (
    SELECT 1 FROM `user`
WHERE user_id=#{userId}
)

mapper.update:
UPDATE `user`
SET nickname = #{nickname}
    , password = #{password}
    , profile_pic = #{pic}
    , status_message = #{statusMessage}
WHERE user_no = #{targetUserNo}

mapper.select:
SELECT leader_no
FROM project
WHERE project_no=#{projectNo}
